INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#pimInfo','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'An attribute relating to print image matching');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsLatitudeRef','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates whether the latitude is north or south latitude. The ASCII value ''N'' indicates north latitude, and ''S'' is south latitude.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#yResolution','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The number of pixels per ResolutionUnit in the ImageLength direction. The same value as XResolution is designated.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#resolution','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'a rational number representing a resolution. Could be a subProperty of other general schema.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#yCbCrCoefficients','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The matrix coefficients for transformation from RGB to YCbCr image data.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#stripByteCounts','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The total number of bytes in each strip. With JPEG compressed data this designation is not needed and is omitted.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#recOffset','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'An attribute relating to recording offset');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#artist','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Person who created the image');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDateStamp','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'date and time information relative to UTC (Coordinated Universal Time). The record format is "YYYY:MM:DD" while converted to W3C-DTF to use in RDF');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#date','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'a date information. Usually saved as YYYY:MM:DD (HH:MM:SS) format in Exif data, but represented here as W3C-DTF format');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#pimBrightness','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Brightness info for print image matching');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#shutterSpeedValue','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Shutter speed. The unit is the APEX (Additive System of Photographic Exposure) setting');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#focalPlaneXResolution','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The number of pixels in the image width (X) direction per FocalPlaneResolutionUnit on the camera focal plane.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDestDistance','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The distance to the destination point.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#subjectLocation','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The location of the main subject in the scene. The value of this tag represents the pixel at the center of the main subject relative to the left edge, prior to rotation processing as per the Rotation tag. The first value indicates the X column number and second indicates the Y row number.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#subjectDistanceRange','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The distance to the subject, such as Macro, Close View or Distant View.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#interoperabilityIndex','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates the identification of the Interoperability rule. ''R98'' = conforming to R98 file specification of Recommended Exif Interoperability Rules (ExifR98) or to DCF basic file stipulated by Design Rule for Camera File System. ''THM'' = conforming to DCF thumbnail file stipulated by Design rule for Camera File System.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#interopInfo','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,replace('An attribute relating to Interoperability. Tags stored in\nInteroperability IFD may be defined dependently to each Interoperability rule.','\n',char(10)));
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#imageWidth','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Image width. The number of columns of image data, equal to the number of pixels per row. In JPEG compressed data a JPEG marker is used instead of this tag.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#width','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Width of an object');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#software','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The name and version of the software or firmware of the camera or image input device used to generate the image.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#flash','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The status of flash when the image was shot.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsSpeed','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The speed of GPS receiver movement.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDOP','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The GPS DOP (data degree of precision). An HDOP value is written during two-dimensional measurement, and PDOP during three-dimensional measurement.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#customRendered','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The use of special processing on image data, such as rendering geared to output. When special processing is performed, the reader is expected to disable or minimize any further processing.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDestLatitude','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Latitude of destination, expressed as three values giving the degrees, minutes, and seconds, respectively.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsSatellites','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The GPS satellites used for measurements. This tag can be used to describe the number of satellites, their ID number, angle of elevation, azimuth, SNR and other information in ASCII notation. The format is not specified. If the GPS receiver is incapable of taking measurements, value of the tag shall be set to NULL.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#subSecTimeOriginal','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'DateTimeOriginal subseconds');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#dateAndOrTime','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'An attribute relating to Date and/or Time');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#make','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Manufacturer of image input equipment');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#whiteBalance','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The white balance mode set when the image was shot.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#apertureValue','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The lens aperture. The unit is the APEX value.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsAltitude','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The altitude based on the reference in GPSAltitudeRef. Altitude is expressed as one RATIONAL value. The reference unit is meters.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDifferential','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates whether differential correction is applied to the GPS receiver.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#flashEnergy','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The strobe energy at the time the image is captured, as measured in Beam Candle Power Seconds (BCPS).');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#relatedImageLength','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Related image length');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#cfaPattern','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The color filter array (CFA) geometric pattern of the image sensor when a one-chip color area sensor is used. It does not apply to all sensing methods.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#exposureMode','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'the exposure mode set when the image was shot. In auto-bracketing mode, the camera shoots a series of frames of the same scene at different exposure settings.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#fileSource','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The image source. If a DSC recorded the image, this tag value of this tag always be set to 3, indicating that the image was recorded on a DSC.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDestLongitudeRef','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Reference for longitude of destination');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#focalLengthIn35mmFilm','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The equivalent focal length assuming a 35mm film camera, in mm. A value of 0 means the focal length is unknown. Note that this tag differs from the FocalLength tag.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#tagid','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The Exif tag number with context prefix, such as IFD type or maker name');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#tag_number','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The Exif tag number');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#saturation','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The direction of saturation processing applied by the camera when the image was shot.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDestDistanceRef','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates the unit used to express the distance to the destination point. ''K'', ''M'' and ''N'' represent kilometers, miles and knots.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#compressedBitsPerPixel','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Information specific to compressed data. The compression mode used for a compressed image is indicated in unit bits per pixel.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#imageConfig','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'An attribute relating to Image Configuration');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#focalLength','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The actual focal length of the lens, in mm. Conversion is not made to the focal length of a 35 mm film camera.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#dateTime','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The date and time of image creation. In this standard it is the date and time the file was changed.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsInfo_IFD_Pointer','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A pointer to the GPS IFD, which is a set of tags for recording GPS information.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#ifdPointer','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A tag that refers a child IFD');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#jpegInterchangeFormat','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The offset to the start byte (SOI) of JPEG compressed thumbnail data. This is not used for primary image JPEG data.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#exposureProgram','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The class of the program used by the camera to set exposure when the picture is taken.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#sceneType','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The type of scene. If a DSC recorded the image, this tag value shall always be set to 1, indicating that the image was directly photographed.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#bitsPerSample','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The number of bits per image component. In this standard each component of the image is 8 bits, so the value for this tag is 8. See also SamplesPerPixel. In JPEG compressed data a JPEG marker is used instead of this tag.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#exifVersion','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Exif Version');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#focalPlaneYResolution','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The number of pixels in the image height (Y) direction per FocalPlaneResolutionUnit on the camera focal plane.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#brightnessValue','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The value of brightness. The unit is the APEX value. Ordinarily it is given in the range of -99.99 to 99.99. Note that if the numerator of the recorded value is FFFFFFFF.H, Unknown shall be indicated.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#flashpixVersion','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The Flashpix format version supported by a FPXR file. If the FPXR function supports Flashpix format Ver. 1.0, this is indicated similarly to ExifVersion by recording "0100" as 4-byte ASCII.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsVersionID','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The version of GPSInfoIFD. The version is given as 2.2.0.0. This tag is mandatory when GPSInfo tag is present.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#deviceSettingDescription','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Information on the picture-taking conditions of a particular camera model. The tag is used only to indicate the picture-taking conditions in the reader.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsTrackRef','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The reference for giving the direction of GPS receiver movement. ''T'' denotes true direction and ''M'' is magnetic direction.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsSpeedRef','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The unit used to express the GPS receiver speed of movement. ''K'' ''M'' and ''N'' represents kilometers per hour, miles per hour, and knots.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#meteringMode','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Metering mode, such as CenterWeightedAverage, Spot, MultiSpot,Pattern, Partial etc.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#exposureTime','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Exposure time, given in seconds (sec).');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#seconds','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'a mesurement of time length with unit of second');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsDestBearing','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The bearing to the destination point. The range of values is from 0.00 to 359.99.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#compression','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The compression scheme used for the image data. When a primary image is JPEG compressed, this designation is not necessary and is omitted. When thumbnails use JPEG compression, this tag value is set to 6.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#sceneCaptureType','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The type of scene that was shot. It can also be used to record the mode in which the image was shot, such as Landscape, Portrait etc. Note that this differs from the scene type (SceneType) tag.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#pimContrast','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Contrast info for print image matching');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#relatedFile','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Tag Relating to Related File Information');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#_unknown','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'An Exif tag whose meaning is not known');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#dateTimeOriginal','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The date and time when the original image data was generated. For a DSC the date and time the picture was taken are recorded.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsAltitudeRef','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates the altitude used as the reference altitude. If the reference is sea level and the altitude is above sea level, 0 is given. If the altitude is below sea level, a value of 1 is given and the altitude is indicated as an absolute value in the GPSAltitude tag. The reference unit is meters.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#yCbCrPositioning','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The position of chrominance components in relation to the luminance component. This field is designated only for JPEG compressed data or uncompressed YCbCr data.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#oecf','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates the Opto-Electric Conversion Function (OECF) specified in ISO 14524. OECF is the relationship between the camera optical input and the image values.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#model','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Model of image input equipment');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#pimSaturation','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Saturation info for print image matching');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsAreaInformation','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A character string recording the name of the GPS area. The first byte indicates the character code used, and this is followed by the name of the GPS area.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#colorSpace','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The color space information tag (ColorSpace) is always recorded as the color space specifier. Normally sRGB (=1) is used to define the color space based on the PC monitor conditions and environment.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#pixelXDimension','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Information specific to compressed data. When a compressed file is recorded, the valid width of the meaningful image shall be recorded in this tag, whether or not there is padding data or a restart marker. This tag should not exist in an uncompressed file.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#printImageMatching_IFD_Pointer','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A pointer to the print image matching IFD');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#planarConfiguration','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates whether pixel components are recorded in chunky or planar format. In JPEG compressed files a JPEG marker is used instead of this tag. If this field does not exist, the TIFF default of 1 (chunky) is assumed.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsTrack','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The direction of GPS receiver movement. The range of values is from 0.00 to 359.99.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#photometricInterpretation','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Pixel composition. In JPEG compressed data a JPEG marker is used instead of this tag.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#pimSharpness','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Sharpness info for print image matching');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#spectralSensitivity','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Indicates the spectral sensitivity of each channel of the camera used. The tag value is an ASCII string compatible with the standard developed by the ASTM Technical committee.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#focalPlaneResolutionUnit','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The unit for measuring FocalPlaneXResolution and FocalPlaneYResolution. This value is the same as the ResolutionUnit.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#exif_IFD_Pointer','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A pointer to the Exif IFD, which is a set of tags for recording Exif-specific attribute information.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#meter','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A length with unit of meter');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#contrast','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The direction of contrast processing applied by the camera when the image was shot.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#subjectArea','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The location and area of the main subject in the overall scene.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#spatialFrequencyResponse','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'This tag records the camera or input device spatial frequency table and SFR values in the direction of image width, image height, and diagonal direction, as specified in ISO 12233.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#subjectDistance','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The distance to the subject, given in meters. Note that if the numerator of the recorded value is FFFFFFFF.H, Infinity shall be indicated; and if the numerator is 0, Distance unknown shall be indicated.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#imageDescription','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A character string giving the title of the image. It may be a comment such as "1988 company picnic" or the like. Two-byte character codes cannot be used. When a 2-byte code is necessary, the Exif Private tag UserComment is to be used.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsMapDatum','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The geodetic survey data used by the GPS receiver. If the survey data is restricted to Japan, the value of this tag is ''TOKYO'' or ''WGS-84''. If a GPS Info tag is recorded, it is strongly recommended that this tag be recorded.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#subSecTime','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'DateTime subseconds');
