INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#subseconds','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A tag used to record fractions of seconds for a date property');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#rowsPerStrip','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The number of rows per strip. This is the number of rows in the image of one strip when an image is divided into strips. With JPEG compressed data this designation is not needed and is omitted.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#pixelYDimension','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'Information specific to compressed data. When a compressed file is recorded, the valid height of the meaningful image shall be recorded in this tag, whether or not there is padding data or a restart marker. This tag should not exist in an uncompressed file. Since data padding is unnecessary in the vertical direction, the number of lines recorded in this valid image height tag will in fact be the same as that recorded in the SOF.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsMeasureMode','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'The GPS measurement mode. ''2'' means two-dimensional measurement and ''3'' means three-dimensional measurement is in progress.');
INSERT INTO properties VALUES('http://www.w3.org/2003/12/exif/ns#gpsProcessingMethod','http://www.w3.org/2003/12/exif/ns#',NULL,NULL,NULL,NULL,'A character string recording the name of the method used for location finding. The first byte indicates the character code used, and this is followed by the name of the method.');
